import React, {useState, useEffect} from "react";
import BackendErrorsMessages from 'components/backendErrorsMessages';

const ArticleForm = ({onSubmit, errors, initialValue}) => {

    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [body, setBody] = useState('');
    const [tagList, setTagList] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();

        // console.log('data: ', title, description, body, tagList);
        onSubmit({title, description, body, tagList: tagList.split(' ')});
    }

    useEffect(() => {
        if(!initialValue){
            return
        }
        // console.log(initialValue);
        setTitle(initialValue.title);
        setDescription(initialValue.description);
        setBody(initialValue.body);
        setTagList(initialValue.tagList.join(' '));
    }, [initialValue]);

    return (
        <div className="container">
            { errors && (<BackendErrorsMessages errors={errors}/>) }
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="title" className="form-label">
                        Title
                    </label>
                    <input
                        type="text"
                        className="form-control"
                        id="title"
                        placeholder="please enter title"
                        value={title}
                        onChange={e => setTitle(e.target.value)}
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="description" className="form-label">
                        Description
                    </label>
                    <input
                        type="text"
                        className="form-control"
                        id="description"
                        placeholder="please enter description"
                        value={description}
                        onChange={e => setDescription(e.target.value)}
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="body" className="form-label">
                        Text article
                    </label>
                    <textarea
                        className="form-control"
                        id="body" rows="3"
                        placeholder="please enter text article"
                        value={body}
                        onChange={e => setBody(e.target.value)}
                    >

                    </textarea>
                </div>
                <div className="mb-3">
                    <label htmlFor="tags" className="form-label">
                        tags article
                    </label>
                    <input
                        type="text"
                        className="form-control"
                        id="tags"
                        placeholder="please enter tags article"
                        value={tagList}
                        onChange={e => setTagList(e.target.value)}
                    />
                </div>
                <button type="submit" className="btn btn-success">
                    Submit
                </button>
            </form>
        </div>
    )
}

export default ArticleForm;
