import React from "react";

import 'components/BackendErrorsMessages.css'

const BackendErrorsMessages = ({errors}) => {

    // console.log(errors)
    const errorsList = errors && Object.keys(errors)
                                .map(title => `${title}: ${errors[title].join(', ')}`);

    // console.log(errorsList);
    return(
        <div>
            <ul className="error-list">
                {
                    errorsList && errorsList.map(error => (
                        <li key={error}>{error}</li>
                    ))
                }
            </ul>
        </div>
    )
}

export default BackendErrorsMessages;
