import React, { useState, useEffect, useContext } from "react";
import {Link, Redirect} from "react-router-dom";
import useFetch from 'hooks/useFetch';
import useLocalstorage from 'hooks/useLocalstorage';

import { CurrentUserContext } from 'context/currentUserContext';
import BackendErrorsMessages from 'components/backendErrorsMessages';

import './authentification.css'

const Authentification = (props) => {
    const isLogin = props.match.path === '/login';
    const apiUser = isLogin ? '/users/login' : '/users';
    const pageName = isLogin ? 'Login' : 'Sign Up';

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [username, setUsername] = useState('');
    const [isSubmitingSuccess, setIsSubmittingSuccess] = useState(false);
    const [currentUserState, dispatch] = useContext(CurrentUserContext);
    const [{response, isLoading, errors}, doFetch] = useFetch(apiUser);
    const [token, setToken] = useLocalstorage('token');

    // console.log('currentUserState: ', currentUserState);

    const handleSubmit = (event) => {
        event.preventDefault();
        const user = isLogin ? { email, password } : { email, password, username };
        doFetch({
            method: 'post',
            data: {
                user,
            }
        })
    };

    useEffect(() => {
        if(!response) {
            return;
        }
        console.log('success');
        const { token } = response.data.user;
        setToken(token);
        setIsSubmittingSuccess(true);
        dispatch({ type: 'SET_AUTHORIZED', payload: response.data.user });
        setEmail('');
        setPassword('');
        if(!isLogin) setUsername('');
    }, [response, setToken, dispatch]);

    if(isSubmitingSuccess) {
        return <Redirect to="/" />;
    }

    return(
        <div className="container">
            <div className="row">
                <div className="col-md-6 offset-md-3">
                    <h1>{pageName}</h1>
                    {
                        isLogin && (
                            <Link
                                className="signup"
                                aria-current="page"
                                to="/signup"
                            >
                                back sign up
                            </Link>
                        )
                    }
                    <form onSubmit={handleSubmit}>
                        <div className="mb-3">
                            { errors && (<BackendErrorsMessages errors={errors.errors}/>) }
                        </div>
                        {
                            !isLogin && (
                                <div className="mb-3">
                                    <label htmlFor="username" className="form-label">username</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        id="username"
                                        aria-describedby="emailHelp"
                                        placeholder="username"
                                        value={username}
                                        onChange={ e => setUsername(e.target.value) }
                                    />
                                </div>
                            )
                        }
                        <div className="mb-3">
                            <label htmlFor="email" className="form-label">Email address</label>
                            <input
                                type="email"
                                className="form-control"
                                id="email"
                                aria-describedby="emailHelp"
                                placeholder="email"
                                value={email}
                                onChange={ e => setEmail(e.target.value) }
                            />
                        </div>
                        <div className="mb-3">
                            <label htmlFor="password" className="form-label">Password</label>
                            <input
                                type="password"
                                className="form-control"
                                id="password"
                                placeholder="password"
                                value={password}
                                autoComplete="on"
                                onChange={ e => setPassword(e.target.value) }
                            />
                        </div>
                        <button
                            type="submit"
                            className="btn btn-success"
                            disabled={isLoading}
                        >
                            {pageName}
                        </button>
                    </form>
                </div>
            </div>
        </div>
    )
}

export default Authentification;
