const queryString = require('query-string');

const range = (start, end) => [...Array(end).keys()].map(elem => elem + start);

const limit = 2;

const getPaginator = search => {
    const baseQuery = queryString.parse(search);
    const currentPage = baseQuery.page ? baseQuery.page : 1;
    const offset = currentPage * limit - limit;
    return { currentPage, offset }
}

export {
    range,
    getPaginator,
    limit,
};
